import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { readDeck } from "../../../utils/api";
import NavigationBar from "../../NavigationBar";
import NotFound from "../../NotFound";
import StudyCards from "./StudyCards";

function Study() {
  const { deckId } = useParams();
  const [deck, setDeck] = useState({});
  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal)
      .then(setDeck)
      .catch((e) => {
        console.log("Error inside Study.js: ", e.message);
        return <NotFound />;
      });
    return () => abortController.abort();
  }, [deckId]);

  if (!deck.id) {
    return <NotFound />;

    if (cards.length <= 2) {
      return (
        <div>
          <h2>Not enough cards.</h2>
          <p>
            You need at least 3 cards to study. There are {cards.length} in the
            deck.
          </p>
          <button type="button" className="btn btn-primary  mr-2">
            {" "}
            Add Cards
            <a href={`/decks/${cards[0].deckId}/cards/new`}>.</a>
          </button>
        </div>
      );
    }
  }
  return (
    <div>
      <NavigationBar navItems={[deck.name, "Study"]} />
      <h2>Study : {deck.name}</h2>
      <StudyCards cards={deck.cards} />
    </div>
  );
}

export default Study;
